// src/components/Dashboard/Navbar.js

import React from 'react';
import { AppBar, Toolbar, Typography, Box, InputBase, Button } from '@mui/material';
import { Search, AccountCircle } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';

const Navbar = () => {
    const navigate = useNavigate();
    const token = localStorage.getItem('token');

    const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/signin');
    };

    return (
        <AppBar position="sticky" color="default" elevation={0} sx={{ borderBottom: '1px solid #e0e0e0' }}>
            <Toolbar>
                <Typography variant="h6" sx={{ flexGrow: 1 }}>
                    CourseConnect
                </Typography>
                {token ? (
                    <>
                        <Button color="inherit" onClick={() => navigate('/dashboard')}>Dashboard</Button>
                        <Button color="inherit" onClick={handleLogout}>Sign Out</Button>
                    </>
                ) : (
                    <>
                        <Button color="inherit" onClick={() => navigate('/signin')}>Sign In</Button>
                        <Button color="inherit" onClick={() => navigate('/signup')}>Sign Up</Button>
                    </>
                )}
                <Box sx={{ mx: 2, display: 'flex', alignItems: 'center', backgroundColor: '#f4f4f9', borderRadius: '4px', px: 2 }}>
                    <Search sx={{ mr: 1 }} />
                    <InputBase placeholder="Search for a user" />
                </Box>
                {token && <AccountCircle sx={{ ml: 2 }} />}
            </Toolbar>
        </AppBar>
    );
};

export default Navbar;
