// src/components/Dashboard/Sidebar.js

import React from 'react';
import { List, ListItem, ListItemText, Typography, Box, Drawer } from '@mui/material';
import { Group, Event, Assignment, School, Leaderboard, Folder } from '@mui/icons-material';

const Sidebar = () => {
    const items = [
        { text: 'Study Buddy', icon: <Group /> },
        { text: 'Projects', icon: <Folder /> },
        { text: 'Calendar', icon: <Event /> },
        { text: 'Tasks', icon: <Assignment /> },
        { text: 'Courses', icon: <School /> },
        { text: 'Leaderboard', icon: <Leaderboard /> },
        { text: 'Resources', icon: <Folder /> },
    ];

    return (
        <Drawer
            variant="permanent"
            sx={{
                display: { xs: 'none', md: 'block' }, // Hide on small screens
                '& .MuiDrawer-paper': { boxSizing: 'border-box', width: 240 },
            }}
            open
        >
            <Box sx={{ width: 240, bgcolor: '#f4f4f9', p: 2, minHeight: '100vh' }}>
                <Typography variant="h6" sx={{ mb: 2 }}>Updates</Typography>
                <List>
                    {items.map((item, index) => (
                        <ListItem button key={index}>
                            {item.icon}
                            <ListItemText primary={item.text} sx={{ ml: 2 }} />
                        </ListItem>
                    ))}
                </List>
            </Box>
        </Drawer>
    );
};

export default Sidebar;
