// src/components/Dashboard/Sidebar.js

import React from 'react';
import { List, ListItem, ListItemText, Typography, Box } from '@mui/material';
import { Home, Group, Event, Assignment, School, Leaderboard, Folder } from '@mui/icons-material';

const Sidebar = () => {
    const items = [
        { text: 'Study Buddy', icon: <Group /> },
        { text: 'Projects', icon: <Folder /> },
        { text: 'Calendar', icon: <Event /> },
        { text: 'Tasks', icon: <Assignment /> },
        { text: 'Courses', icon: <School /> },
        { text: 'Leaderboard', icon: <Leaderboard /> },
        { text: 'Resources', icon: <Folder /> },
    ];

    return (
        <Box sx={{ width: '250px', bgcolor: '#f4f4f9', p: 2, minHeight: '100vh' }}>
            <Typography variant="h6" sx={{ mb: 2 }}>Updates</Typography>
            <List>
                {items.map((item, index) => (
                    <ListItem button key={index}>
                        {item.icon}
                        <ListItemText primary={item.text} sx={{ ml: 2 }} />
                    </ListItem>
                ))}
            </List>
        </Box>
    );
};

export default Sidebar;
