// src/components/Dashboard/Sidebar.js

import React from 'react';
import { List, ListItem, ListItemIcon, ListItemText, Box, Typography } from '@mui/material';
import { Group, Event, Assignment, School, Leaderboard, Folder } from '@mui/icons-material';

const Sidebar = () => {
    const items = [
        { text: 'Study Buddy', icon: <Group /> },
        { text: 'Projects', icon: <Folder /> },
        { text: 'Calendar', icon: <Event /> },
        { text: 'Tasks', icon: <Assignment /> },
        { text: 'Courses', icon: <School /> },
        { text: 'Leaderboard', icon: <Leaderboard /> },
        { text: 'Resources', icon: <Folder /> },
    ];

    return (
        <Box sx={{ width: { xs: '100%', md: '20%' }, bgcolor: '#f4f4f9', p: 2, height: '100vh', boxShadow: 1 }}>
            <Typography variant="h6" sx={{ mb: 2 }}>Navigation</Typography>
            <List>
                {items.map((item, index) => (
                    <ListItem button key={index} sx={{ mb: 1, '&:hover': { bgcolor: '#e0f7fa' } }}>
                        <ListItemIcon>{item.icon}</ListItemIcon>
                        <ListItemText primary={item.text} />
                    </ListItem>
                ))}
            </List>
        </Box>
    );
};

export default Sidebar;
